import {SEARCH_PRODUCT} from "../constants";

const initialState = {
    products: [],
    offset: 0
}

export default function(state = initialState, action){
    switch(action.type){
        case SEARCH_PRODUCT:
            return {
                ...state,
                products: action.payload.results,
                offset: action.payload.paging.offset
            }
        default: return state;
    }
}
