import {SEARCH_PRODUCT} from "./constants";
import axios from "axios";

export function getProducts(query, offset = 0) {
    return function(dispatch){
        axios.get(`localhost:3001/search?q=${query}&offset=${offset}`)
        .then(res => {
            dispatch({ type: SEARCH_PRODUCT, payload: res.data})
        })
    }
}
