import {SEARCH_PRODUCT, NEW_PRODUCT, USED_PRODUCT} from "./constants";
import axios from "axios";

export function getProducts(query, offset) {
    return function(dispatch){
        axios.get(`http://localhost:3001/search?q=${query}&offset=${offset}`)
        .then(res => {
            console.log(res.data)
            dispatch({ type: SEARCH_PRODUCT, payload: res.data})
        })
        .catch(err => {
            console.log(err)
        })
    }
}

export function newProducts(condition){
    return function(dispatch){
        dispatch({ type: NEW_PRODUCT, payload: condition})
    }
}
