import {SEARCH_PRODUCT} from "./constants";
import axios from "axios";

export function getProducts(query, offset) {
    const paginacion = offset++
    console.log(`http://localhost:3001/search?q=${query}&offset=${paginacion}`)
    return function(dispatch){
        axios.get(`http://localhost:3001/search?q=${query}&offset=${paginacion}`)
        .then(res => {
            dispatch({ type: SEARCH_PRODUCT, payload: res.data})
        })
        .catch(err => {
            console.log(err)
        })
    }
}
